import React, { useEffect, useState } from "react";

const pageStyle: React.CSSProperties = {
  background: "#f6f6f6",
  minHeight: "100vh",
  padding: "32px 0",
  fontFamily: "'Inter', 'Segoe UI', Arial, sans-serif",
};

const headerStyle: React.CSSProperties = {
  margin: "0 0 24px 0",
  fontWeight: 800,
  fontSize: "2.5rem",
  color: "#333",
  textAlign: "left",
  paddingLeft: "24px",
  letterSpacing: "0.02em",
  fontFamily: "'Poppins', sans-serif",
};

const searchStyle: React.CSSProperties = {
  display: "flex",
  alignItems: "center",
  margin: "0 0 24px 0",
  paddingLeft: "24px",
};

const inputStyle: React.CSSProperties = {
  padding: "12px 18px",
  borderRadius: "12px",
  border: "1px solid #e0e0e0",
  fontSize: "1rem",
  width: "60%",
  marginRight: "12px",
  background: "#f9f9f9",
  fontFamily: "'Inter', 'Segoe UI', Arial, sans-serif",
};

const cardContainerStyle: React.CSSProperties = {
  display: "flex",
  flexWrap: "wrap",
  gap: "32px",
  paddingLeft: "24px",
};

const cardStyle: React.CSSProperties = {
  background: "#fff",
  borderRadius: "24px",
  boxShadow: "0 4px 24px rgba(0,0,0,0.06)",
  padding: "32px",
  width: "320px",
  display: "flex",
  flexDirection: "column",
  alignItems: "center",
  marginBottom: "32px",
};

const imgStyle: React.CSSProperties = {
  width: "160px",
  height: "160px",
  objectFit: "cover",
  borderRadius: "18px",
  marginBottom: "18px",
  background: "#f6f6f6",
  border: "1px solid #eee",
};

const priceStyle: React.CSSProperties = {
  fontWeight: 700,
  fontSize: "1.2rem",
  color: "#c9a063",
  margin: "12px 0",
  fontFamily: "'Inter', 'Segoe UI', Arial, sans-serif",
  overflow: "hidden",
  textOverflow: "ellipsis",
  whiteSpace: "nowrap",
};

const titleStyle: React.CSSProperties = {
  fontWeight: 700,
  fontSize: "1.15rem",
  marginBottom: "8px",
  fontFamily: "'Inter', 'Segoe UI', Arial, sans-serif",
  color: "#222",
  textAlign: "center",
  overflow: "hidden",
  textOverflow: "ellipsis",
  whiteSpace: "nowrap",
  maxWidth: "280px",
};

const descStyle: React.CSSProperties = {
  color: "#888",
  fontSize: "0.98rem",
  marginBottom: "16px",
  fontFamily: "'Inter', 'Segoe UI', Arial, sans-serif",
  textAlign: "center",
  overflow: "hidden",
  textOverflow: "ellipsis",
  display: "-webkit-box",
  WebkitLineClamp: 3,
  WebkitBoxOrient: "vertical",
  maxWidth: "280px",
  height: "auto",
};

const infoRowStyle: React.CSSProperties = {
  display: "flex",
  justifyContent: "space-between",
  width: "100%",
  marginBottom: "8px",
  fontSize: "0.98rem",
  color: "#888",
  overflow: "hidden",
  textOverflow: "ellipsis",
  whiteSpace: "nowrap",
};

const badgeStyle: React.CSSProperties = {
  background: "#e0f2f1",
  color: "#2e7d32",
  borderRadius: "8px",
  padding: "4px 12px",
  fontWeight: 600,
  fontSize: "0.95rem",
  marginBottom: "8px",
  fontFamily: "'Inter', 'Segoe UI', Arial, sans-serif",
};

const filterContainerStyle: React.CSSProperties = {
  display: "flex",
  gap: "12px",
  paddingLeft: "24px",
  marginBottom: "20px",
};

const filterPillStyle: React.CSSProperties = {
  background: "#e8eaf6",
  color: "#3f51b5",
  borderRadius: "20px",
  padding: "8px 16px",
  cursor: "pointer",
  fontSize: "0.9rem",
  fontWeight: 600,
  transition: "background-color 0.3s ease",
};

const ListingPage = () => {
  const [listings, setListings] = useState<any[]>([]);
  const [search, setSearch] = useState("");
  const [activeCategory, setActiveCategory] = useState<string | null>(null);

  useEffect(() => {
    fetch("http://localhost:9090/lighting/api/products")
      .then((res) => res.json())
      .then((data) => {
        setListings(data);
      })
      .catch(() => setListings([]));
  }, []);

  const handleCategoryClick = (category: string | null) => {
    setActiveCategory(category);
  };

  const filteredListings = listings.filter((item) => {
    const searchTermMatch =
      item.name?.toLowerCase().includes(search.toLowerCase()) ||
      item.title?.toLowerCase().includes(search.toLowerCase()) ||
      item.categoryDescription?.toLowerCase().includes(search.toLowerCase());

    let categoryMatch = true;
    if (activeCategory) {
      categoryMatch = item.categoryId === activeCategory;
    }

    return searchTermMatch && categoryMatch;
  });

  const uniqueCategories = ["1.0", "2.0", "11.0"];

  return (
    <div style={pageStyle}>
      <div style={headerStyle}>Find Your Dream Lights</div>
      <div style={searchStyle}>
        <input
          style={inputStyle}
          type="text"
          placeholder="Search by name, location, or category"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
      </div>

      <div style={filterContainerStyle}>
        <div
          style={{
            ...filterPillStyle,
            ...(activeCategory === null
              ? { background: "#c5cae9", color: "#333" }
              : {}),
          }}
          onClick={() => handleCategoryClick(null)}
        >
          All
        </div>
        {uniqueCategories.map((category) => (
          <div
            key={category}
            style={{
              ...filterPillStyle,
              ...(activeCategory === category
                ? { background: "#c5cae9", color: "#333" }
                : {}),
            }}
            onClick={() => handleCategoryClick(category)}
          >
            {category}
          </div>
        ))}
      </div>

      <div style={cardContainerStyle}>
        {filteredListings.map((item) => (
          <div key={item.id} style={cardStyle}>
            <img
              src={
                item.imageUrl && item.imageUrl !== "default.jpg"
                  ? item.imageUrl
                  : "https://img.icons8.com/ios-filled/200/light.png"
              }
              alt={item.name}
              style={imgStyle}
            />
            <div style={badgeStyle}>{item.categoryDescription || "Lighting"}</div>
            <div style={titleStyle}>{item.title || item.name}</div>
            <div style={descStyle}>{item.description}</div>
            <div style={infoRowStyle}>
              <span>Order Code: {item.orderCode}</span>
              <span>Qty: {item.quantity}</span>
            </div>
            <div style={priceStyle}>â‚¹{item.price} INR</div>
            <button
              style={{
                background: "#a892ee",
                color: "#fff",
                border: "none",
                borderRadius: "12px",
                padding: "12px 28px",
                cursor: "pointer",
                fontWeight: 700,
                fontSize: "1rem",
                marginTop: "auto",
                fontFamily: "'Inter', 'Segoe UI', Arial, sans-serif",
                alignSelf: "center",
              }}
            >
              Add to Cart
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ListingPage;
