import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import Lottie from "lottie-react";
import splashLottie from "../components/lottie/splash-lottie.json";

const pageStyle: React.CSSProperties = {
  background: "linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%)",
  minHeight: "100vh",
  width: "100vw",
  fontFamily: "'Poppins', 'Inter', 'Roboto', Arial, sans-serif",
  padding: 0,
  margin: 0,
  boxSizing: "border-box",
  border: "none",
  position: "fixed",
  top: 0,
  left: 0,
  right: 0,
  bottom: 0,
  zIndex: 0,
  overflowY: "auto", // Enable vertical scrolling everywhere
};

const lampContainerStyle: React.CSSProperties = {
  width: "100%",
  maxWidth: 400,
  margin: "48px auto 0 auto",
  display: "flex",
  flexDirection: "column",
  alignItems: "center",
};

const lampSVGStyle: React.CSSProperties = {
  width: "180px",
  height: "180px",
  marginBottom: "-32px",
};

const cardContainerStyle: React.CSSProperties = {
  display: "flex",
  justifyContent: "center",
  alignItems: "flex-start",
  gap: "24px", // Reduced gap between cards
  padding: "32px 12px 32px 12px", // Reduced padding
  width: "100%",
  flexWrap: "wrap",
  maxWidth: "1000px", // Slightly reduced max width
  margin: "0 auto",
  boxSizing: "border-box",
  overflowX: "hidden",
  minHeight: "0",
  maxHeight: "none", // Remove maxHeight to avoid clipping
};

const cardStyle: React.CSSProperties = {
  background: "rgba(255,255,255,0.97)",
  borderRadius: "24px", // slightly smaller radius
  boxShadow: "0 6px 24px rgba(0,0,0,0.10)",
  padding: "20px 12px", // reduced padding
  width: "180px", // reduced card width
  textAlign: "center",
  cursor: "pointer",
  transition: "transform 0.22s cubic-bezier(.4,2,.6,1), box-shadow 0.22s",
  color: "#222",
  border: "2px solid #fff",
  position: "relative",
  overflow: "hidden",
  marginBottom: "20px", // reduced bottom margin
  minHeight: 160, // reduced card height
  display: "flex",
  flexDirection: "column",
  justifyContent: "flex-end",
};

const cardHoverStyle: React.CSSProperties = {
  transform: "scale(1.05) translateY(-8px)",
  boxShadow: "0 16px 48px rgba(0,0,0,0.14)",
  border: "2px solid #fda085",
};

const iconStyle: React.CSSProperties = {
  fontSize: "2.1rem", // Reduced icon size
  marginBottom: "12px",
  color: "#fda085",
  filter: "drop-shadow(0 2px 8px #f6d36555)",
};

const contentWrapperStyle: React.CSSProperties = {
  maxWidth: "1000px",
  margin: "0 auto",
  padding: "0 12px",
  boxSizing: "border-box",
  width: "100%",
};

const headerStyle: React.CSSProperties = {
  margin: "0 0 12px 0",
  fontWeight: 500, // Thinner font weight for a sleeker look
  fontSize: "2.1rem",
  color: "#a78bfa",   // Lighter violet for a softer appearance
  textAlign: "center",
  letterSpacing: "0.01em",
  fontFamily: "'Montserrat', 'Poppins', 'Inter', Arial, sans-serif",
  textShadow: "none",
  paddingLeft: 0,
  boxSizing: "border-box",
  fontStyle: "normal",
};

const subHeaderStyle: React.CSSProperties = {
  textAlign: "center", // Center align
  marginBottom: "24px",
  color: "#7b8aaf",
  fontSize: "1.3rem",
  fontWeight: 500,
  letterSpacing: "0.02em",
  textShadow: "0 2px 12px rgba(0,0,0,0.10)",
  paddingLeft: 0,
  boxSizing: "border-box",
};

const ElectronSplashScreen: React.FC = () => {
  const [categories, setCategories] = useState<any[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setLoading(true);
    fetch("http://localhost:9090/lighting/api/categories", {
      headers: { Accept: "application/json" },
    })
      .then((res) => res.json())
      .then((data) => setCategories(data))
      .catch(() => setCategories([]))
      .finally(() => setLoading(false));
  }, []);

  const history = useHistory();

  const handleProductTypeClick = (
    categoryId: string,
    categoryDisplayName: string,
    productTypeId: string,
    productTypeHeading: string
  ) => {
    history.push(
      `/listings?categoryId=${encodeURIComponent(categoryId)}&productTypeId=${encodeURIComponent(productTypeId)}`
    );
  };

  return (
    <div style={pageStyle}>
      <div style={lampContainerStyle}>
        {/* Splash Lottie Animation */}
        <Lottie
          animationData={splashLottie}
          loop
          autoplay
          style={{ width: 180, height: 180, marginBottom: "-32px" }}
        />
      </div>
      <div style={contentWrapperStyle}>
        <div style={headerStyle}>ElectronInnovations</div>
        <div style={subHeaderStyle}>Ignite Your Imagination With Light</div>
        {loading ? (
          <div style={{ textAlign: "center", margin: "32px 0" }}>Loading...</div>
        ) : (
          <div>
            {categories.map((cat) => (
              <div key={cat.id} style={{ marginBottom: 32 }}>
                <div
                  style={{
                    fontWeight: 700,
                    fontSize: "1.5rem",
                    margin: "24px 0 12px 0",
                    textAlign: "left",
                  }}
                >
                  {cat.categoryDisplayName}
                </div>
                <div style={cardContainerStyle}>
                  {cat.items.map((type: any) => (
                    <div
                      key={type.productTypeId}
                      style={{
                        ...cardStyle,
                        background: type.imageUrl
                          ? `url(${type.imageUrl}) center center/cover no-repeat`
                          : cardStyle.background,
                        position: "relative",
                        color: "#222",
                        cursor: "pointer",
                        border: "2px solid #fff",
                        boxShadow: "0 6px 24px rgba(0,0,0,0.10)",
                        transition: "transform 0.22s cubic-bezier(.4,2,.6,1), box-shadow 0.22s",
                        overflow: "hidden",
                        marginBottom: "24px",
                        minHeight: 220,
                        display: "flex",
                        flexDirection: "column",
                        justifyContent: "flex-end",
                      }}
                      onClick={() =>
                        handleProductTypeClick(
                          cat.id,
                          cat.categoryDisplayName,
                          type.productTypeId,
                          type.productTypeHeading
                        )
                      }
                    >
                      {/* Overlay for readability */}
                      <div
                        style={{
                          position: "absolute",
                          inset: 0,
                          background: type.imageUrl
                            ? "linear-gradient(0deg, rgba(255,255,255,0.92) 40%, rgba(255,255,255,0.12) 100%)"
                            : "none",
                          zIndex: 1,
                        }}
                      />
                      <div style={{ position: "relative", zIndex: 2 }}>
                        <div
                          style={{
                            fontWeight: 700,
                            fontSize: "1.15rem",
                            marginBottom: 8,
                            minHeight: 52,
                            maxHeight: 52,
                            maxWidth: 180,
                            width: 180,
                            overflow: "hidden",
                            display: "-webkit-box",
                            WebkitLineClamp: 2,
                            WebkitBoxOrient: "vertical",
                            textOverflow: "ellipsis",
                            textAlign: "center",
                            marginLeft: "auto",
                            marginRight: "auto",
                            alignItems: "center",
                            justifyContent: "center",
                            color: "#181818",
                          }}
                          title={type.productTypeHeading}
                        >
                          {type.productTypeHeading}
                        </div>
                        <div
                          style={{
                            marginTop: 12,
                            color: "#a77fd9",
                            background: "none",
                            border: "none",
                            borderRadius: 12,
                            padding: 0,
                            width: "100%",
                            fontWeight: 500,
                            fontSize: "1rem",
                            cursor: "pointer",
                            fontFamily: "'Poppins', 'Inter', Arial, sans-serif",
                            opacity: 0.85,
                            letterSpacing: "0.01em",
                            transition: "color 0.2s",
                            userSelect: "none",
                            display: "flex",
                            alignItems: "center",
                            justifyContent: "center",
                            gap: 8,
                          }}
                        >
                          <span>Explore</span>
                          <span style={{ fontSize: "1.15em", display: "flex", alignItems: "center" }}>â†’</span>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default ElectronSplashScreen;
