import React, { useState, useRef, useEffect } from "react";
import { useLocation, useHistory } from "react-router-dom";

// --- Theme Colors ---
const purple = "#7b1fa2";
const blue = "#3949ab";
const bgGradient = "linear-gradient(135deg, #e3e6ff 0%, #f3e7fa 100%)";
const cardBg = "rgba(255,255,255,0.98)";

// --- Styles ---
const pageStyle: React.CSSProperties = {
  display: "flex",
  minHeight: "100vh",
  background: bgGradient,
  fontFamily: "'Poppins', 'Inter', Arial, sans-serif",
};

const leftStyle: React.CSSProperties = {
  flex: 1,
  padding: "48px 48px 40px 80px",
  background: "none",
  display: "flex",
  flexDirection: "column",
  justifyContent: "flex-start",
  minWidth: 340,
  maxWidth: 540,
};

const formCardStyle: React.CSSProperties = {
  background: cardBg,
  borderRadius: 24,
  boxShadow: "0 8px 32px rgba(123,31,162,0.08)",
  padding: "36px 32px 32px 32px",
  width: "100%",
  maxWidth: 480,
  margin: "0 auto",
  display: "flex",
  flexDirection: "column",
  gap: 0,
};

const sectionTitleStyle: React.CSSProperties = {
  fontWeight: 700,
  fontSize: "1.5rem",
  margin: "0 0 24px 0",
  color: purple,
  letterSpacing: "0.01em",
};

const labelStyle: React.CSSProperties = {
  fontWeight: 600,
  marginBottom: 6,
  fontSize: "1rem",
  color: blue,
};

const inputStyle: React.CSSProperties = {
  width: "100%",
  minWidth: 0,
  padding: "13px 16px",
  borderRadius: 10,
  border: `1.5px solid ${purple}`,
  marginBottom: 18,
  fontSize: "1.08rem",
  fontFamily: "'Inter', Arial, sans-serif",
  background: "#f7f7fa",
  boxSizing: "border-box",
  display: "block",
  outline: "none",
  transition: "border 0.2s",
};

const inputRowStyle: React.CSSProperties = {
  display: "flex",
  gap: 16,
  marginBottom: 18,
};

const errorStyle: React.CSSProperties = {
  color: "#d32f2f",
  fontSize: "0.98rem",
  marginBottom: 12,
};

const submitBtnStyle: React.CSSProperties = {
  marginTop: 18,
  background: `linear-gradient(90deg, ${purple} 0%, ${blue} 100%)`,
  color: "#fff",
  border: "none",
  borderRadius: 12,
  padding: "16px 0",
  width: "100%",
  cursor: "pointer",
  fontWeight: 700,
  fontSize: "1.13rem",
  fontFamily: "'Poppins', 'Inter', Arial, sans-serif",
  boxShadow: "0 2px 12px rgba(123,31,162,0.10)",
  transition: "background 0.2s",
  display: "block",
  letterSpacing: "0.02em",
};

const rightStyle: React.CSSProperties = {
  width: 400,
  background: "rgba(255,255,255,0.97)",
  padding: "40px 0 0 0",
  borderLeft: `2px solid ${purple}22`,
  height: "100vh",
  display: "flex",
  flexDirection: "column",
  position: "relative",
  boxShadow: "0 0 32px 0 rgba(123,31,162,0.04)",
};

const cartListStyle: React.CSSProperties = {
  flex: 1,
  overflowY: "auto",
  padding: "0 32px 0 32px",
  marginBottom: 0,
  minHeight: 0,
};

const cartItemStyle: React.CSSProperties = {
  display: "flex",
  alignItems: "center",
  gap: 14,
  marginBottom: 18,
  borderBottom: "1px solid #eee",
  paddingBottom: 12,
};

const cartImgStyle: React.CSSProperties = {
  width: 48,
  height: 48,
  objectFit: "cover",
  borderRadius: 8,
  background: "#f3f6fa",
  border: "1px solid #eee",
};

const cartTitleStyle: React.CSSProperties = {
  fontWeight: 600,
  fontSize: "1.08rem",
  color: purple,
};

const cartPriceStyle: React.CSSProperties = {
  fontWeight: 700,
  color: blue,
  fontSize: "1.08rem",
};

const summaryWrapperStyle: React.CSSProperties = {
  position: "sticky",
  bottom: 0,
  left: 0,
  right: 0,
  background: "rgba(255,255,255,0.97)",
  borderTop: `2px solid ${purple}22`,
  textAlign: "right",
  fontWeight: 700,
  fontSize: "1.15rem",
  color: purple,
  height: 64,
  display: "flex",
  alignItems: "center",
  justifyContent: "flex-end",
  padding: "0 32px",
  zIndex: 2,
  boxShadow: "0 -2px 16px 0 rgba(123,31,162,0.04)",
};

function getCartTotal(cart: any[]) {
  return cart.reduce((sum, item) => sum + item.price * item.qty, 0);
}

const API_URL = "http://localhost:9090/lighting/api/orders/place-order";

const CustomerDetailPage: React.FC = () => {
  const location = useLocation<{ cart: any[] }>();
  const history = useHistory();
  const cart = location.state?.cart || [];

  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [phone, setPhone] = useState("");
  const [email, setEmail] = useState("");
  const [address, setAddress] = useState("");
  const [pincode, setPincode] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  async function handleSubmit(e: React.FormEvent) {
    e.preventDefault();
    if (!firstName.trim() || !lastName.trim() || !phone.trim() || !address.trim() || !pincode.trim()) {
      setError("All fields are required.");
      return;
    }
    setError("");
    setLoading(true);

    // Prepare order items for API
    const orderItems = cart.map(item => ({
      productId: item.id,
      quantity: item.qty,
    }));

    try {
      const response = await fetch(API_URL, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(orderItems),
      });

      if (!response.ok) {
        throw new Error("Order failed. Please try again.");
      }

      // Optionally handle response data
      // const data = await response.json();

      setLoading(false);
      alert("Order placed successfully!");
      history.replace("/listings"); // or redirect to a success page
    } catch (err) {
  setLoading(false);
  if (err instanceof Error) {
    setError(err.message || "Order failed. Please try again.");
  } else {
    setError("Order failed. Please try again.");
  }
}
}

  if (!cart.length) {
    history.replace("/listings");
    return null;
  }

  return (
    <div style={pageStyle}>
      <div style={leftStyle}>
        <form style={formCardStyle} onSubmit={handleSubmit}>
          <div style={sectionTitleStyle}>Contact Information</div>
          <label style={labelStyle}>Full Name</label>
          <input
            style={inputStyle}
            type="text"
            value={firstName}
            onChange={e => setFirstName(e.target.value)}
            placeholder="Full Name"
            autoComplete="name"
            required
          />
          <label style={labelStyle}>Phone Number</label>
          <input
            style={inputStyle}
            type="tel"
            value={phone}
            onChange={e => setPhone(e.target.value)}
            placeholder="Phone Number"
            autoComplete="tel"
            required
          />
          <label style={labelStyle}>Email</label>
          <input
            style={inputStyle}
            type="email"
            value={email}
            onChange={e => setEmail(e.target.value)}
            placeholder="Email"
            autoComplete="email"
            required
          />
          <div style={inputRowStyle}>
            <div style={{ flex: 2 }}>
              <label style={labelStyle}>Address</label>
              <input
                style={inputStyle}
                type="text"
                value={address}
                onChange={e => setAddress(e.target.value)}
                placeholder="Address"
                autoComplete="street-address"
                required
              />
            </div>
            <div style={{ flex: 1 }}>
              <label style={labelStyle}>Pincode</label>
              <input
                style={inputStyle}
                type="text"
                value={pincode}
                onChange={e => setPincode(e.target.value)}
                placeholder="Pincode"
                autoComplete="postal-code"
                required
              />
            </div>
          </div>
          {error && <div style={errorStyle}>{error}</div>}
          <button style={submitBtnStyle} type="submit" disabled={loading}>
            {loading ? "Placing Order..." : "Place Order"}
          </button>
        </form>
      </div>
      <div style={rightStyle}>
        <div style={{ ...sectionTitleStyle, fontSize: "1.25rem", margin: "0 0 18px 32px" }}>Your Cart</div>
        <div style={cartListStyle}>
          {cart.map(item => (
            <div key={item.id} style={cartItemStyle}>
              <img
                src={item.imageUrl && item.imageUrl !== "default.jpg"
                  ? item.imageUrl
                  : "https://img.icons8.com/ios-filled/200/light.png"}
                alt={item.name}
                style={cartImgStyle}
              />
              <div style={{ flex: 1 }}>
                <div style={cartTitleStyle}>{item.title || item.name}</div>
                <div style={cartPriceStyle}>₹{item.price} × {item.qty}</div>
              </div>
            </div>
          ))}
        </div>
        <div style={summaryWrapperStyle}>
          Subtotal: <span style={{ color: purple, fontWeight: 800, marginLeft: 8 }}>₹{getCartTotal(cart)} INR</span>
        </div>
      </div>
    </div>
  );
};

export default CustomerDetailPage;
