import React, { useState, useEffect } from 'react';

const pageStyle: React.CSSProperties = {
  background: "#f6f6f6",
  minHeight: "100vh",
  padding: "32px 0",
  fontFamily: "'Inter', 'Segoe UI', Arial, sans-serif",
};

const headerStyle: React.CSSProperties = {
  margin: "0 0 24px 0",
  fontWeight: 800,
  fontSize: "2.5rem",
  color: "#333",
  textAlign: "center",
  letterSpacing: "0.02em",
  fontFamily: "'Poppins', sans-serif",
};

const categoryContainerStyle: React.CSSProperties = {
  display: "flex",
  justifyContent: "center",
  gap: "32px",
  padding: "24px",
};

const categoryCardStyle: React.CSSProperties = {
  background: "#fff",
  borderRadius: "24px",
  boxShadow: "0 4px 24px rgba(0,0,0,0.06)",
  padding: "32px",
  width: "320px",
  textAlign: "center",
  cursor: "pointer",
  transition: "transform 0.3s ease",
  textDecoration: 'none',
  color: 'inherit'
};

interface Category {
  id: string;
  name: string;
}

const ElectronSplashScreen: React.FC = () => {
  const categories: Category[] = [
  { id: "1.0", name: "Category 1.0" },
  { id: "2.0", name: "Category 2.0" },
  { id: "11.0", name: "Category 11.0" },
  ];
  const [listings, setListings] = useState<any[]>([]);

  useEffect(() => {
  fetch("http://localhost:9090/lighting/api/products")
  .then((res) => res.json())
  .then((data) => {
  // Filter the data to include only items with category IDs 1.0, 2.0, or 11.0
  const filteredData = data.filter((item: any) =>
  categories.some((category) => category.id === item.categoryId)
  );
  setListings(filteredData);
  })
  .catch(() => setListings([]));
  }, [categories]); // Add categories as a dependency

  const handleCategoryClick = (categoryId: string) => {
        const filteredListings = listings.filter(item => item.categoryId === categoryId);
        navigate(`/listings?category=${categoryId}&data=${encodeURIComponent(JSON.stringify(filteredListings))}`);
    };

  return (
  <div style={pageStyle}>
  <div style={headerStyle}>Luminex Innovations</div>
  <div style={{textAlign: "center", marginBottom: "20px"}}>Ignite Your Imagination With Light</div>

  <div style={categoryContainerStyle}>
  {categories.map((category) => (
  <div
  key={category.id}
  style={categoryCardStyle}
  onClick={() => handleCategoryClick(category.id)}
  >
  <h2>{category.name}</h2>
  <p>Explore lights in {category.name}</p>
  </div>
  ))}
  </div>
  </div>
  );
};

export default ElectronSplashScreen;

export {};
