import React, { useEffect, useState } from "react";

const pageStyle: React.CSSProperties = {
  background: "#f6f6f6",
  minHeight: "100vh",
  padding: "32px 0",
  fontFamily: "'Inter', 'Segoe UI', Arial, sans-serif",
};

const headerStyle: React.CSSProperties = {
  margin: "0 0 24px 0",
  fontWeight: 700,
  fontSize: "2rem",
  color: "#222",
  textAlign: "left",
  paddingLeft: "24px",
  letterSpacing: "0.01em",
  fontFamily: "'Inter', 'Segoe UI', Arial, sans-serif",
};

const searchStyle: React.CSSProperties = {
  display: "flex",
  alignItems: "center",
  margin: "0 0 24px 0",
  paddingLeft: "24px",
};

const inputStyle: React.CSSProperties = {
  padding: "12px 18px",
  borderRadius: "12px",
  border: "1px solid #e0e0e0",
  fontSize: "1rem",
  width: "60%",
  marginRight: "12px",
  background: "#f9f9f9",
  fontFamily: "'Inter', 'Segoe UI', Arial, sans-serif",
};

const cardContainerStyle: React.CSSProperties = {
  display: "flex",
  gap: "32px",
  paddingLeft: "24px",
};

const cardStyle: React.CSSProperties = {
  background: "#fff",
  borderRadius: "24px",
  boxShadow: "0 4px 24px rgba(0,0,0,0.06)",
  padding: "32px",
  width: "280px",
  display: "flex",
  flexDirection: "column",
  alignItems: "center",
};

const imgStyle: React.CSSProperties = {
  width: "140px",
  height: "140px",
  objectFit: "cover",
  borderRadius: "18px",
  marginBottom: "18px",
  background: "#f6f6f6",
};

const priceStyle: React.CSSProperties = {
  fontWeight: 700,
  fontSize: "1.3rem",
  color: "#c9a063",
  margin: "16px 0",
  fontFamily: "'Inter', 'Segoe UI', Arial, sans-serif",
};

const ListingPage = () => {
  const [listings, setListings] = useState<any[]>([]);
  const [search, setSearch] = useState("");

  useEffect(() => {
    // Replace with your local API endpoint
    fetch("http://localhost:5000/api/listings")
      .then((res) => res.json())
      .then((data) => setListings(data))
      .catch(() => setListings([]));
  }, []);

  const filteredListings = listings.filter(
    (item) =>
      item.name.toLowerCase().includes(search.toLowerCase()) ||
      item.location.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div style={pageStyle}>
      <div style={headerStyle}>Find Your Dream Lights</div>
      <div style={searchStyle}>
        <input
          style={inputStyle}
          type="text"
          placeholder="Search based on location"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
      </div>
      <div style={cardContainerStyle}>
        {filteredListings.map((item) => (
          <div key={item.id} style={cardStyle}>
            <img src={item.imageUrl} alt={item.name} style={imgStyle} />
            <div style={{ fontWeight: 700, fontSize: "1.15rem", marginBottom: "8px", fontFamily: "'Inter', 'Segoe UI', Arial, sans-serif" }}>{item.name}</div>
            <div style={{ color: "#888", marginBottom: "8px", fontSize: "1rem" }}>{item.location}</div>
            <div style={{ color: "#b6a77c", fontSize: "0.98rem", marginBottom: "8px" }}>{item.description}</div>
            <div style={priceStyle}>${item.price} USD</div>
            <button
              style={{
                background: "#c9a063",
                color: "#fff",
                border: "none",
                borderRadius: "12px",
                padding: "12px 28px",
                cursor: "pointer",
                fontWeight: 700,
                fontSize: "1rem",
                marginTop: "16px",
                fontFamily: "'Inter', 'Segoe UI', Arial, sans-serif"
              }}
            >
              Contact Seller
            </button>
          </div>
        ))}
      </div>
      <style>
        {`
          @font-face {
            font-family: 'SF Pro Display';
            src: url('/fonts/SF-Pro-Display.ttf') format('truetype');
            font-weight: 400;
            font-style: normal;
          }
        `}
      </style>
    </div>
  );
};

export default ListingPage;
