import React, { useState } from "react";
import { useLocation, useHistory } from "react-router-dom";

const pageStyle: React.CSSProperties = {
  display: "flex",
  minHeight: "100vh",
  background: "#fafbfc",
  fontFamily: "'Poppins', 'Inter', Arial, sans-serif",
};

const leftStyle: React.CSSProperties = {
  flex: 1,
  padding: "48px 40px 40px 80px",
  background: "#fff",
  display: "flex",
  flexDirection: "column",
  justifyContent: "flex-start",
  minWidth: 340,
  maxWidth: 520,
};

const rightStyle: React.CSSProperties = {
  width: 400,
  background: "#f7f7fa",
  padding: "40px 32px 32px 32px",
  borderLeft: "1px solid #eee",
  height: "100vh",
  display: "flex",
  flexDirection: "column",
  position: "relative",
};

const labelStyle: React.CSSProperties = {
  fontWeight: 600,
  marginBottom: 6,
  fontSize: "1rem",
};

const inputStyle: React.CSSProperties = {
  width: "100%",
  minWidth: 0,
  padding: "12px 16px",
  borderRadius: 10,
  border: "1px solid #e0e0e0",
  marginBottom: 18,
  fontSize: "1rem",
  fontFamily: "'Inter', Arial, sans-serif",
  background: "#f9f9fb",
  boxSizing: "border-box",
  display: "block",
};

const errorStyle: React.CSSProperties = {
  color: "#d32f2f",
  fontSize: "0.95rem",
  marginBottom: 12,
};

const sectionTitleStyle: React.CSSProperties = {
  fontWeight: 700,
  fontSize: "1.3rem",
  margin: "0 0 18px 0",
};

const cartItemStyle: React.CSSProperties = {
  display: "flex",
  alignItems: "center",
  gap: 14,
  marginBottom: 18,
  borderBottom: "1px solid #eee",
  paddingBottom: 12,
};

const cartImgStyle: React.CSSProperties = {
  width: 48,
  height: 48,
  objectFit: "cover",
  borderRadius: 8,
  background: "#f3f6fa",
  border: "1px solid #eee",
};

const cartTitleStyle: React.CSSProperties = {
  fontWeight: 600,
  fontSize: "1.05rem",
};

const cartPriceStyle: React.CSSProperties = {
  fontWeight: 700,
  color: "#7b1fa2",
  fontSize: "1.05rem",
};

// Set a fixed height for the cart list and make it scrollable
const cartListStyle: React.CSSProperties = {
  maxHeight: 320, // Fixed height for scroll area
  overflowY: "auto",
  marginBottom: 0,
  paddingBottom: 0,
};

const summaryWrapperStyle: React.CSSProperties = {
  marginTop: 24,
  background: "#f7f7fa",
  borderTop: "1px solid #e0e0e0",
  textAlign: "right",
  fontWeight: 600,
  fontSize: "1.1rem",
  color: "#7b1fa2",
  height: 48,
  display: "flex",
  alignItems: "center",
  justifyContent: "flex-end",
};

const submitBtnStyle: React.CSSProperties = {
  marginTop: 24,
  background: "#b39ddb",
  color: "#fff",
  border: "none",
  borderRadius: 12,
  padding: "16px 0",
  width: "100%",
  cursor: "pointer",
  fontWeight: 700,
  fontSize: "1.1rem",
  fontFamily: "'Poppins', 'Inter', Arial, sans-serif",
  boxShadow: "0 2px 8px rgba(179,157,219,0.13)",
  transition: "background 0.2s",
  display: "block",
};

function getCartTotal(cart: any[]) {
  return cart.reduce((sum, item) => sum + item.price * item.qty, 0);
}

const CustomerDetailPage: React.FC = () => {
  const location = useLocation<{ cart: any[] }>();
  const history = useHistory();
  const cart = location.state?.cart || [];

  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [phone, setPhone] = useState("");
  const [email, setEmail] = useState("");
  const [error, setError] = useState("");

  function handleSubmit(e: React.FormEvent) {
    e.preventDefault();
    if (!firstName.trim() || !lastName.trim() || !phone.trim()) {
      setError("First name, last name, and phone are required.");
      return;
    }
    setError("");
    // You can handle the submit logic here or navigate to the order status screen
    alert("Order placed!\n" + JSON.stringify({ firstName, lastName, phone, email }, null, 2));
  }

  // Optionally, redirect if cart is empty
  if (!cart.length) {
    history.replace("/");
    return null;
  }

  return (
    <div style={pageStyle}>
      <form style={leftStyle} onSubmit={handleSubmit}>
        <div style={sectionTitleStyle}>Contact</div>
        <label style={labelStyle}>Email</label>
        <input
          style={inputStyle}
          type="email"
          value={email}
          onChange={e => setEmail(e.target.value)}
          placeholder="Email"
        />

        <div style={sectionTitleStyle}>Mail my items</div>
        <label style={labelStyle}>First name *</label>
        <input
          style={inputStyle}
          type="text"
          value={firstName}
          onChange={e => setFirstName(e.target.value)}
          placeholder="First name"
          required
        />
        <label style={labelStyle}>Last name *</label>
        <input
          style={inputStyle}
          type="text"
          value={lastName}
          onChange={e => setLastName(e.target.value)}
          placeholder="Last name"
          required
        />
        <label style={labelStyle}>Phone *</label>
        <input
          style={inputStyle}
          type="tel"
          value={phone}
          onChange={e => setPhone(e.target.value)}
          placeholder="Phone"
          required
        />
        {error && <div style={errorStyle}>{error}</div>}
        <button style={submitBtnStyle} type="submit">
          Place Order
        </button>
      </form>
      <div style={rightStyle}>
        <div style={sectionTitleStyle}>Your Cart</div>
        <div style={cartListStyle}>
          {cart.map(item => (
            <div key={item.id} style={cartItemStyle}>
              <img
                src={item.imageUrl && item.imageUrl !== "default.jpg"
                  ? item.imageUrl
                  : "https://img.icons8.com/ios-filled/200/light.png"}
                alt={item.name}
                style={cartImgStyle}
              />
              <div style={{ flex: 1 }}>
                <div style={cartTitleStyle}>{item.title || item.name}</div>
                <div style={cartPriceStyle}>₹{item.price} × {item.qty}</div>
              </div>
            </div>
          ))}
        </div>
        <div style={summaryWrapperStyle}>
          <div>
            Subtotal: <span style={{ color: "#7b1fa2", fontWeight: 700 }}>₹{getCartTotal(cart)} INR</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CustomerDetailPage;
