import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";


const ListingPage: React.FC = () => {
  const [listings, setListings] = useState<any[]>([]);
  const [search, setSearch] = useState("");
  const location = useLocation();

  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const dataParam = params.get("data");
    if (dataParam) {
      try {
        const parsed = JSON.parse(decodeURIComponent(dataParam));
        setListings(parsed);
      } catch (e) {
        setListings([]);
      }
    } else {
      setListings([]);
    }
  }, [location.search]);

  const filteredListings = listings.filter((item) => {
    const searchTermMatch =
      item.name?.toLowerCase().includes(search.toLowerCase()) ||
      item.title?.toLowerCase().includes(search.toLowerCase()) ||
      item.categoryDescription?.toLowerCase().includes(search.toLowerCase());
    return searchTermMatch;
  });

  return (
    <div style={pageStyle}>
      <div style={headerStyle}>Find Your Dream Lights</div>
      <div style={searchStyle}>
        <input
          style={inputStyle}
          type="text"
          placeholder="Search by name, location, or category"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
        />
      </div>

      {/* Removed broad category pills here */}

      <div style={cardContainerStyle}>
        {filteredListings.map((item) => (
          <div key={item.id} style={cardStyle}>
            <img
              src={
                item.imageUrl && item.imageUrl !== "default.jpg"
                  ? item.imageUrl
                  : "https://img.icons8.com/ios-filled/200/light.png"
              }
              alt={item.name}
              style={imgStyle}
            />
            <div style={badgeStyle}>{item.categoryDescription || "Lighting"}</div>
            <div style={titleStyle}>{item.title || item.name}</div>
            <div style={descStyle}>{item.description}</div>
            <div style={infoRowStyle}>
              <span>Order Code: {item.orderCode}</span>
              <span>Qty: {item.quantity}</span>
            </div>
            <div style={priceStyle}>â‚¹{item.price} INR</div>
            <button
              style={{
                background: "#a892ee",
                color: "#fff",
                border: "none",
                borderRadius: "12px",
                padding: "12px 28px",
                cursor: "pointer",
                fontWeight: 700,
                fontSize: "1rem",
                marginTop: "auto",
                fontFamily: "'Inter', 'Segoe UI', Arial, sans-serif",
                alignSelf: "center",
              }}
            >
              Add to Cart
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ListingPage;
