import React, { useState, useEffect } from "react";
import { useHistory, Prompt } from "react-router-dom";
import Lottie from "lottie-react";
import happyShopperLottie from "./HappyShopperLottie.json";
import { fetchCart, clearCart } from "../components/api/CartApi";

// --- Theme Colors ---
const purple = "#7b1fa2";
const blue = "#3949ab";
const bgGradient = "linear-gradient(135deg, #e3e6ff 0%, #f3e7fa 100%)";
const cardBg = "rgba(255,255,255,0.98)";

// --- Styles ---
const pageStyle: React.CSSProperties = {
  display: "flex",
  minHeight: "100vh",
  background: bgGradient,
  fontFamily: "'Poppins', 'Inter', Arial, sans-serif",
};

const leftStyle: React.CSSProperties = {
  flex: 1,
  padding: "48px 48px 40px 80px",
  background: "none",
  display: "flex",
  flexDirection: "column",
  justifyContent: "flex-start",
  minWidth: 340,
  maxWidth: 540,
};

const formCardStyle: React.CSSProperties = {
  background: cardBg,
  borderRadius: 24,
  boxShadow: "0 8px 32px rgba(123,31,162,0.08)",
  padding: "36px 32px 32px 32px",
  width: "100%",
  maxWidth: 480,
  margin: "0 auto",
  display: "flex",
  flexDirection: "column",
  gap: 0,
};

const sectionTitleStyle: React.CSSProperties = {
  fontWeight: 700,
  fontSize: "1.5rem",
  margin: "0 0 24px 0",
  color: purple,
  letterSpacing: "0.01em",
};

const labelStyle: React.CSSProperties = {
  fontWeight: 600,
  marginBottom: 6,
  fontSize: "1rem",
  color: blue,
};

const inputStyle: React.CSSProperties = {
  width: "100%",
  minWidth: 0,
  padding: "13px 16px",
  borderRadius: 10,
  border: `1.5px solid ${purple}`,
  marginBottom: 18,
  fontSize: "1.08rem",
  fontFamily: "'Inter', Arial, sans-serif",
  background: "#f7f7fa",
  boxSizing: "border-box",
  display: "block",
  outline: "none",
  transition: "border 0.2s",
};

const inputRowStyle: React.CSSProperties = {
  display: "flex",
  gap: 16,
  marginBottom: 18,
};

const errorStyle: React.CSSProperties = {
  color: "#d32f2f",
