function getDisplayPrice(item: any) {
  const discounted = Number(item.discountedPrice);
  if (!isNaN(discounted) && discounted > 0) {
    return discounted;
  }
  return Number(item.price);
}

function getCartTotal(cart: any[]) {
  return cart.reduce((sum, item) => sum + getDisplayPrice(item) * item.qty, 0);
}

const API_URL = "http://localhost:9090/lighting/api/orders/place-order";

const CustomerDetailPage: React.FC = () => {
  const history = useHistory();

  // --- Cart state from backend ---
  const [cart, setCart] = useState<any[]>([]);
  const [cartLoaded, setCartLoaded] = useState(false);

  // --- Form state ---
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [phone, setPhone] = useState("");
  const [email, setEmail] = useState("");
  const [address, setAddress] = useState("");
  const [pincode, setPincode] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const [showSuccess, setShowSuccess] = useState(false);

  // Fetch cart from backend on mount
  useEffect(() => {
    let isMounted = true;
    async function loadCart() {
      const data = await fetchCart();
      if (isMounted) {
        setCart(
          (Array.isArray(data) ? data : []).map((item: any) => ({
            ...item,
            id: item.lineId,
            name: item.productName,
            qty: item.quantity,
            price: item.unitPrice,
          }))
        );
        setCartLoaded(true);
      }
    }
    loadCart();
    return () => {
      isMounted = false;
    };
  }, []);

  // If cart is empty after fetch, redirect
  useEffect(() => {
    if (cartLoaded && cart.length === 0) {
      history.replace("/listings");
    }
  }, [cart, cartLoaded, history]);

  // Block navigation while placing order
  useEffect(() => {
    if (!loading) return;
    const unblock = history.block("Order is being placed. Are you sure you want to leave?");
    const beforeUnload = (e: BeforeUnloadEvent) => {
      e.preventDefault();
      e.returnValue = "";
    };
    window.addEventListener("beforeunload", beforeUnload);
    return () => {
      unblock();
      window.removeEventListener("beforeunload", beforeUnload);
    };
  }, [loading, history]);

  async function handleSubmit(e: React.FormEvent) {
    e.preventDefault();
    if (!firstName.trim() || !phone.trim() || !email.trim()) {
      setError("First name, phone number, and email are required.");
      return;
    }
    setError("");
    setLoading(true);

    const subtotal = getCartTotal(cart);
    const reqBody = {
      customer: {
        firstName,
        lastName,
        email,
        phone,
        address,
        pincode,
      },
      subtotal,
      order: {
        productList: cart.map(item => ({
          id: item.id,
          name: item.title || item.name,
          price: item.price,
          quantity: item.qty,
        })),
      },
    };

    try {
      const response = await fetch(API_URL, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(reqBody),
      });

      if (!response.ok) {
        throw new Error("Order failed. Please try again.");
      }

      // Clear cart after successful order
      await clearCart();
      setCart([]);
      setShowSuccess(true);
      setLoading(false);

      // Optionally, navigate away after a short delay
      setTimeout(() => {
        history.replace("/listings");
      }, 1200);
    } catch (err) {
      setLoading(false);
      setError(err instanceof Error ? err.message : "Order failed. Please try again.");
    }
  }

  // --- Success Dialog Styles ---
  const dialogOverlayStyle: React.CSSProperties = {
    position: "fixed",
    top: 0,
    left: 0,
    width: "100vw",
    height: "100vh",
    background: "rgba(60, 40, 120, 0.25)",
    backdropFilter: "blur(12px)",
    zIndex: 9999,
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    transition: "backdrop-filter 0.6s cubic-bezier(0.4,0,0.2,1), background 0.6s cubic-bezier(0.4,0,0.2,1)",
    opacity: 1,
    pointerEvents: "none",
  };

  const dialogBoxStyle: React.CSSProperties = {
    background: "#fff",
    borderRadius: 20,
    boxShadow: "0 8px 32px rgba(123,31,162,0.18)",
    padding: "28px 20px 24px 20px",
    maxWidth: 300,
    width: "90vw",
    textAlign: "center",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    position: "fixed",
    left: "50%",
    top: "50%",
    transform: "translate(-50%, -50%)",
    opacity: 1,
    transition: "opacity 0.4s",
    zIndex: 10000,
  };

  const lottieStyle: React.CSSProperties = {
    width: 120,
    height: 120,
    marginBottom: 12,
  };

  // Show nothing until cart is loaded (prevents flicker/redirect loop)
  if (!cartLoaded) return null;

  return (
    <div style={pageStyle}>
      <Prompt when={loading} message="Order is being placed. Are you sure you want to leave?" />
      {showSuccess && (
        <>
          <div style={dialogOverlayStyle}></div>
          <div style={dialogBoxStyle}>
            <Lottie
              animationData={happyShopperLottie}
              loop
              autoplay
              style={lottieStyle}
            />
            <h2 style={{ color: purple, margin: "8px 0 8px 0" }}>You’re all set!</h2>
            <div style={{ color: blue, fontSize: "1.08rem", marginBottom: 12 }}>
              You can now pay on the go.<br />Thank you for shopping with us!
            </div>
          </div>
        </>
      )}

      <div style={leftStyle}>
        <form style={formCardStyle} onSubmit={handleSubmit} autoComplete="off">
          <div style={sectionTitleStyle}>Contact Information</div>
          <div style={inputRowStyle}>
            <div style={{ flex: 1 }}>
              <label style={labelStyle}>First Name</label>
              <input
                id="firstName"
                name="firstName"
                style={inputStyle}
                type="text"
                value={firstName}
                onChange={e => setFirstName(e.target.value)}
                placeholder="First Name"
                autoComplete="given-name"
                required
              />
            </div>
            <div style={{ flex: 1 }}>
              <label style={labelStyle}>Last Name</label>
              <input
                id="lastName"
                name="lastName"
                style={inputStyle}
                type="text"
                value={lastName}
                onChange={e => setLastName(e.target.value)}
                placeholder="Last Name"
                autoComplete="family-name"
              />
            </div>
          </div>
          <label style={labelStyle}>Phone Number</label>
          <input
            id="phone"
            name="phone"
            style={inputStyle}
            type="tel"
            value={phone}
            onChange={e => setPhone(e.target.value)}
            placeholder="Phone Number"
            autoComplete="tel"
            required
          />
          <label style={labelStyle}>Email</label>
          <input
            id="email"
            name="email"
            style={inputStyle}
            type="email"
            value={email}
            onChange={e => setEmail(e.target.value)}
            placeholder="Email"
            autoComplete="email"
            required
          />
          <div style={inputRowStyle}>
            <div style={{ flex: 2 }}>
              <label style={labelStyle}>Address</label>
              <input
                id="address"
                name="address"
                style={inputStyle}
                type="text"
                value={address}
                onChange={e => setAddress(e.target.value)}
                placeholder="Address"
                autoComplete="street-address"
              />
            </div>
            <div style={{ flex: 1 }}>
              <label style={labelStyle}>Pincode</label>
              <input
                id="pincode"
                name="pincode"
                style={inputStyle}
                type="text"
                value={pincode}
                onChange={e => setPincode(e.target.value)}
                placeholder="Pincode"
                autoComplete="postal-code"
              />
            </div>
          </div>
          <span style={errorStyle}>{error}</span>
          <button style={submitBtnStyle} type="submit" disabled={loading}>
            {loading ? "Placing Order..." : "Place Order"}
          </button>
        </form>
      </div>
      <div style={rightStyle}>
        <div style={{ ...sectionTitleStyle, fontSize: "1.25rem", margin: "0 0 18px 32px" }}>Your Cart</div>
        <div style={cartListStyle}>
          {cart.map(item => (
            <div key={item.id} style={cartItemStyle}>
              <img
                src={item.imageUrl && item.imageUrl !== "default.jpg"
                  ? item.imageUrl
                  : "https://img.icons8.com/ios-filled/200/light.png"}
                alt={item.name}
                style={cartImgStyle}
              />
              <div style={{ flex: 1 }}>
                <div style={cartTitleStyle}>{item.title || item.name}</div>
                <div style={cartPriceStyle}>
                  {(!isNaN(Number(item.discountedPrice)) && Number(item.discountedPrice) > 0 && item.discountedPrice !== item.price) ? (
                    <>
                      <span style={{ textDecoration: "line-through", color: "#bdbdbd", marginRight: 8, fontWeight: 500 }}>
                        ₹{item.price}
                      </span>
                      <span style={{ color: "#fda085" }}>₹{item.discountedPrice} × {item.qty}</span>
                    </>
                  ) : (
                    <>₹{item.price} × {item.qty}</>
                  )}
                </div>
              </div>
            </div>
          ))}
        </div>
        <div style={summaryWrapperStyle}>
          Subtotal: <span style={{ color: purple, fontWeight: 800, marginLeft: 8 }}>₹{getCartTotal(cart)} INR</span>
        </div>
      </div>
    </div>
  );
};

export default CustomerDetailPage;
