import React, { useEffect, useState } from "react";
import { useParams, useHistory } from "react-router-dom";

const pageStyle: React.CSSProperties = {
  background: "linear-gradient(135deg, #fdf6e3 0%, #f6f8fb 100%)",
  minHeight: "100vh",
  fontFamily: "'Poppins', 'Inter', 'Roboto', Arial, sans-serif",
  display: "flex",
  flexDirection: "column",
  alignItems: "center",
  padding: "0",
};

const cardStyle: React.CSSProperties = {
  background: "#fff",
  borderRadius: "32px",
  boxShadow: "0 8px 32px rgba(0,0,0,0.08)",
  padding: "36px 32px",
  width: "370px",
  marginTop: "48px",
  textAlign: "center",
  display: "flex",
  flexDirection: "column",
  alignItems: "center",
};

const imgWrapperStyle: React.CSSProperties = {
  width: "220px",
  height: "220px",
  background: "linear-gradient(135deg, #f6d365 0%, #fda085 100%)",
  borderRadius: "50%",
  display: "flex",
  alignItems: "center",
  justifyContent: "center",
  margin: "0 auto 32px auto",
  boxShadow: "0 4px 24px rgba(253,160,133,0.10)",
};

const imgStyle: React.CSSProperties = {
  width: "140px",
  height: "140px",
  objectFit: "contain",
  borderRadius: "18px",
  background: "#fffbe7",
  border: "none",
  display: "block",
};

const titleStyle: React.CSSProperties = {
  fontWeight: 700,
  fontSize: "1.45rem",
  marginBottom: "8px",
  fontFamily: "'Poppins', sans-serif",
  color: "#222",
  textAlign: "center",
  maxWidth: "100%",
  whiteSpace: "nowrap",
  overflow: "hidden",
  textOverflow: "ellipsis",
};

const locationStyle: React.CSSProperties = {
  color: "#7b8aaf",
  fontSize: "1.05rem",
  marginBottom: "18px",
  fontFamily: "'Inter', sans-serif",
};

const infoRowStyle: React.CSSProperties = {
  display: "flex",
  alignItems: "center",
  justifyContent: "space-between",
  width: "100%",
  margin: "12px 0",
  fontSize: "1.04rem",
  color: "#888",
  fontFamily: "'Inter', sans-serif",
};

const priceStyle: React.CSSProperties = {
  fontWeight: 700,
  fontSize: "1.3rem",
  color: "#fda085",
  margin: "18px 0 0 0",
  fontFamily: "'Inter', 'Segoe UI', Arial, sans-serif",
};

const buttonStyle: React.CSSProperties = {
  background: "linear-gradient(90deg, #f6d365 0%, #fda085 100%)",
  color: "#fff",
  border: "none",
  borderRadius: "18px",
  padding: "16px 0",
  width: "100%",
  cursor: "pointer",
  fontWeight: 700,
  fontSize: "1.1rem",
  marginTop: "32px",
  fontFamily: "'Poppins', 'Inter', Arial, sans-serif",
  boxShadow: "0 2px 8px rgba(253,160,133,0.13)",
  transition: "background 0.2s",
};

interface Product {
  id: string;
  name: string;
  description: string;
  price: number;
  quantity: number;
  category: string | null;
  imageUrl: string;
  title: string;
  orderCode: string;
  categoryId: string;
  categoryDescription: string;
  [key: string]: any;
}

const ProductDetailPage: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const history = useHistory();
  const [product, setProduct] = useState<Product | null>(null);

  useEffect(() => {
    let isMounted = true;
    fetch(`http://localhost:9090/lighting/api/products/${id}`)
      .then((res) => res.json())
      .then((data) => {
        if (isMounted) setProduct(data);
      })
      .catch(() => {
        if (isMounted) setProduct(null);
      });
    return () => {
      isMounted = false;
    };
  }, [id]);

  if (!product) {
    return (
      <div style={pageStyle}>
        <div style={{ marginTop: 80, color: "#888" }}>Product not found.</div>
      </div>
    );
  }

  return (
    <div style={pageStyle}>
      <div style={cardStyle}>
        <div style={imgWrapperStyle}>
          <img
            src={
              product.imageUrl && product.imageUrl !== "default.jpg"
                ? product.imageUrl
                : "https://img.icons8.com/ios-filled/200/light.png"
            }
            alt={product.name}
            style={imgStyle}
          />
        </div>
        <div style={titleStyle}>{product.title || product.name}</div>
        <div style={locationStyle}>
          {product.categoryDescription} &middot; {product.location || product.city || ""}
        </div>
        <div style={infoRowStyle}>
          <span>Height: {product.height || "窶能

Booked!")}>
          Book Now
        </button>
        <button
          style={{
            ...buttonStyle,
            background: "#f3f6fa",
            color: "#fda085",
            marginTop: 16,
            boxShadow: "none",
            border: "1px solid #fda085",
          }}
          onClick={() => history.goBack()}
        >
          Back
        </button>
      </div>
    </div>
  );
};

export default ProductDetailPage;

export {};
